import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from '@testing-library/react';
import Person from './Silvany/Andreas'
// import Userinput from './Userinput/Userinput';
// import Useroutput from './Useroutput/Useroutput';


class App extends Component {
  state = {
    team: [
      { name: 'andreas', age: '21' },
      { name: 'hasiholan', age: '22' },
      { name: 'yesisca', age: '20' },
    ],
    showNama: false,
  }

  switchTeam = (namaBaru) => {
    // console.log('it clicked');
    this.setState(
      {
        team: [
          { name: 'Andreas', age: '25' },
          { name: 'Silvany', age: '24' },
          { name: namaBaru, age: '1' },
        ]
      }
    )
  }

  penghusan = (indexperorang) =>{
    const team = this.state.team;
    team.splice(indexperorang, 1);
    this.setState({team: team});
  }

  scrollName = () => {
    const apakah = this.state.showNama;
    this.setState({ showNama: !apakah });
  }

  perubahanNama = (pemicu) => {
    this.setState(
      {
        team: [
          { name: 'Andreas', age: '25' },
          { name: 'Silvany', age: '24' },
          { name: pemicu.target.value, age: '1' },
        ]
      }
    )
  }
  render() {
    // const gaya = {
    //   backgroundColor:'#c4c2c1',
    //   font : 'inherit',
    //   border: '3px solid grey',
    //   padding: '8px',  
    //   cursor: 'pointer' 
    // };

    let namakita = null;
    if (this.state.showNama) {
      namakita =
        (
          <div>
            {this.state.team.map((kelompok,index)=>{
              return <Person 
              click = {() =>this.penghusan(index)}
              name={kelompok.name}
              age={kelompok.age} />
            })}
          </div>
        )
    }


    return (
      <div className="App">
        {/* <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" />
            <p>
              Edit <code>src/App.js</code> and save to reload.
            </p>
            <a
              className="App-link"
              href="https://reactjs.org"
              target="_blank"
              rel="noopener noreferrer"
            >
              Learn React
            </a>
          </header> */}
        <h1>Hi myname is andreas</h1>
        <p>This realy work</p>
        <button
          className="Button"
          //  style={gaya} 
          onClick={this.scrollName}> Switch person</button>

        {namakita}
      </div>
    );
    //return React.createElement('div',{className: 'App'}, React.createElement('h1',null,'hi , i\'m using whatsapp'));

  }
}

export default App;
